import { setRef } from '@mui/material';
import * as React from 'react';
// import { ThemeContext } from '../../Main';
// import { workingcontext } from '../../pages/Home';

const GetSVG = ((play: boolean, size: number, fill: string) => {
    return (
        !play ?
            <svg
                xmlns="http://www.w3.org/2000/svg"
                width={size}
                height={size}
                fill="currentColor"
                // class="bi bi-skip-forward-circle"
                viewBox="0 0 16 16">
                <circle cx="8" cy="8" r="7" fill={fill} />
                <polygon points="6.5, 6 6.5, 10 10.5, 8" fill="white" />
            </svg>
            :
            <svg
                // @click="player_or_pause"
                xmlns="http://www.w3.org/2000/svg"
                width={size}
                height={size}
                fill="currentColor"
                // class="bi bi-pause-circle" 
                viewBox="0 0 16 16">
                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                <path d="M5 6.25a1.25 1.25 0 1 1 2.5 0v3.5a1.25 1.25 0 1 1-2.5 0v-3.5zm3.5 0a1.25 1.25 0 1 1 2.5 0v3.5a1.25 1.25 0 1 1-2.5 0v-3.5z" />
            </svg>)
})
const Play = ((props: any) => {
    const theme = React.useContext(ThemeContext);
    // const working = React.useContext(workingcontext);

    const [size, setSize] = React.useState(props.size ? props.size : 10);

    const [SVG, setSVG] = React.useState(<svg
        xmlns="http://www.w3.org/2000/svg"
        width={size}
        height={size}
        fill="currentColor"
        // class="bi bi-skip-forward-circle"
        viewBox="0 0 16 16">
        <circle cx="8" cy="8" r="7" fill={theme.color} />
        <polygon points="6.5, 6 6.5, 10 10.5, 8" fill="black" />
    </svg>)

    React.useEffect(() => {
        if (!props.focused) return;
        setSVG(GetSVG(props.isplaying, size, theme.backdark));
    }, [props.isplaying, size]);

    const handleplay = ((event: React.MouseEvent<HTMLDivElement>) => {
        // console.log('handleplay : ', event);

        if (props.focused) {
            event.preventDefault();
            event.stopPropagation();
            event.nativeEvent.stopImmediatePropagation();
        }
        props.handleplay();

        setSVG(GetSVG(props.isplaying, size, theme.backdark));
    });

    const handleenter = (() => {
        console.log('enter');
        setSize(2 * size);
    });

    const handleleave = (() => {
        console.log('leave');
        setSize(props.size ? props.size : 10);
    });

    return (
        <div
            style={{ padding: 4 }}
            onClick={handleplay}
            onMouseEnter={handleenter}
            onMouseLeave={handleleave}
        >
            {SVG}
        </div>)
})

export default Play
